const fetchChannels = (setChannelList, workspaceID) => {
  const item = localStorage.getItem('user');
  if (!item) {
    return;
  }
  const user = JSON.parse(item);
  const bearerToken = user ? user.accessToken : '';
  if (workspaceID === undefined) {
    return;
  }
  fetch(`/v0/channel/${workspaceID}`, {
    method: 'get',
    headers: new Headers({
      'Authorization': `Bearer ${bearerToken}`,
      'Content-Type': 'application/x-www-form-urlencoded',
    }),
  })
    .then((response) => {
      if (!response.ok) {
        throw response;
      }
      return response.json();
    })
    .then((arr) => {
      setChannelList(arr);
    })
    .catch((error) => {
      console.log(error);
      setChannelList([]);
    });
};

/**
 * @return {object} JSX
 */
function NestedList() {
  const classes = useStyles();
  const {value, value2} = React.useContext(WorkspaceContext);
  const [workspaceList] = value;
  const [workspace] = value2;
  const [channelList, setChannelList] = React.useState([]);
  const [channelOpen, setChannelOpen] = React.useState(true);
  const [msgOpen, setMsgOpen] = React.useState(true);

  const toggleOpenChannels = () => {
    setChannelOpen(!channelOpen);
  };

  const toggleOpenMsg = () => {
    setMsgOpen(!msgOpen);
  };

  const workspaceID = () => {
    for (let i = 0; i < workspaceList.length; i++) {
      if (workspace === workspaceList[i].name) {
        return workspaceList[i].id;
      }
    }
  };

  const wsID = workspaceID();

  React.useEffect(() => {
    fetchChannels(setChannelList, wsID);
  }, [wsID]);

  const displayChannels = () => {
    const arr = [];
    for (let i = 0; i < channelList.length; i++) {
      const name = channelList[i]['name'];
      const jsx =
        <ListItem
          key={name}
          button
          className={classes.nested}>
          <ListItemIcon>
            <BlurOnOutlinedIcon />
          </ListItemIcon>
          <ListItemText primary={name} />
        </ListItem>;
      arr.push(jsx);
    }
    return arr;
  };

  return (
    <List
      className={classes.list}
      component="nav"
      aria-labelledby="nested-list-subheader">
      <ListItem
        key={'channels'}
        button
        onClick={toggleOpenChannels}>
        {channelOpen ? <ExpandLess /> : <ExpandMore />}
        <ListItemText primary="Channels" />
      </ListItem>
      <Collapse in={channelOpen} timeout="auto" unmountOnExit>
        <List component="div" disablePadding>
          {displayChannels()}
        </List>
      </Collapse>
      <ListItem
        key={'directMessages'}
        button
        onClick={toggleOpenMsg}>
        {msgOpen ? <ExpandLess /> : <ExpandMore />}
        <ListItemText primary="Direct Messages" />
      </ListItem>
      <Collapse in={msgOpen} timeout="auto" unmountOnExit>
        <List component="div" disablePadding>
          <ListItem
            key={'davidHenry'}
            button
            className={classes.nested}>
            <ListItemIcon>
              <AccountCircleTwoToneIcon />
            </ListItemIcon>
            <ListItemText primary="David Henry" />
          </ListItem>
        </List>
      </Collapse>
    </List>
  );
}
