import React from 'react';
import AppBar from '@material-ui/core/AppBar';
import {makeStyles} from '@material-ui/core/styles';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import AccountCircle from '@material-ui/icons/AccountCircle';
import MenuItem from '@material-ui/core/MenuItem';
import Menu from '@material-ui/core/Menu';
import ArrowDropDownCircleIcon from '@material-ui/icons/ArrowDropDownCircle';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';
import Collapse from '@material-ui/core/Collapse';
import ExpandLess from '@material-ui/icons/ExpandLess';
import ExpandMore from '@material-ui/icons/ExpandMore';
import BlurOnOutlinedIcon from '@material-ui/icons/BlurOnOutlined';
import BottomNavigation from '@material-ui/core/BottomNavigation';
import BottomNavigationAction from '@material-ui/core/BottomNavigationAction';
import HomeTwoToneIcon from '@material-ui/icons/HomeTwoTone';
import ForumTwoToneIcon from '@material-ui/icons/ForumTwoTone';
import AlternateEmailTwoToneIcon
  from '@material-ui/icons/AlternateEmailTwoTone';
import SearchTwoToneIcon from '@material-ui/icons/SearchTwoTone';
import PersonOutlineTwoToneIcon from '@material-ui/icons/PersonOutlineTwoTone';
import AccountCircleTwoToneIcon from '@material-ui/icons/AccountCircleTwoTone';
// import Badge from "@material-ui/core/Badge";
// import Avatar from "@material-ui/core/Avatar";

const useStyles = makeStyles((theme) => ({
  appBar: {
    flexGrow: 1,
    width: `100%`,
    margin: `0px`,
    background: `#4A154B`,
    height: `50px`,
  },
  toolbar: theme.mixins.toolbar,
  menuButton: {
    marginRight: theme.spacing(1),
    marginBottom: theme.spacing(1),
    // height: 40,
  },
  title: {
    flexGrow: 1,
    fontSize: `18px`,
    height: 40,
    fontFamily: 'Verdana',
    // fontWeight: 'bold',
  },
  list: {
    marginTop: 50,
    width: `100%`,
    backgroundColor: theme.palette.background.paper,
  },
  nested: {
    paddingLeft: theme.spacing(4),
  },
  bottom: {
    width: '95%',
    position: 'fixed',
    bottom: 0,
  },
}));

/**
 * @param {*} setWorkspace
 * @param {*} setError
 */
const fetchWorkspace = (setWorkspace) => { // setError
  const item = localStorage.getItem('user');
  if (!item) {
    return;
  }
  const user = JSON.parse(item);
  const bearerToken = user ? user.accessToken : '';
  fetch('/v0/workspace', {
    method: 'get',
    headers: new Headers({
      'Authorization': `Bearer ${bearerToken}`,
      'Content-Type': 'application/x-www-form-urlencoded',
    }),
  })
    .then((response) => {
      if (!response.ok) {
        throw response;
      }
      return response.json();
    })
    .then((arr) => {
      setWorkspace(arr);
      // setError('');
    })
    .catch((error) => {
      console.log(error);
      setWorkspace([]);
      // setError('There is an Error!');
    });
};

/**
 * @return {object} JSX
 */
function Homepage() {
  return (
    <div>
      <MenuAppBar/>
      <NestedList/>
      <LabelBottomNavigation/>
    </div>
  );
}

/**
 * @return {*}
 */
function MenuAppBar() {
  const classes = useStyles();
  const [anchorEl, setAnchorEl] = React.useState(null);
  const open = Boolean(anchorEl);

  // const user = JSON.parse(localStorage.getItem('user'));
  const [workspace, setWorkspace] = React.useState([]);
  // const [name, setName] = React.useState(user ? user.name : '');
  // const [error, setError] = React.useState('');

  // const logout = () => {
  //   localStorage.removeItem('user');
  //   setWorkspace([]);
  //   setName('');
  //   setError('Logged Out');
  // };

  React.useEffect(() => {
    fetchWorkspace(setWorkspace);
  }, []);

  const handleMenu = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleClose = () => {
    setAnchorEl(null);
  };

  return (
    <AppBar
      position="fixed"
      className={classes.appBar}
      elevation={3}>
      <Toolbar className={classes.toolbar}>
        <Typography variant="h6" className={classes.title}>
          {workspace}
        </Typography>
        {
          <div>
            <ArrowDropDownCircleIcon
              className={classes.menuButton}
              aria-label="account of current user"
              aria-controls="menu-appbar"
              aria-haspopup="true"
              onClick={handleMenu}
              color="inherit"
            >
              <AccountCircle />
            </ArrowDropDownCircleIcon>
            <Menu
              id="menu-appbar"
              anchorEl={anchorEl}
              anchorOrigin={{
                vertical: 'top',
                horizontal: 'right',
              }}
              keepMounted
              transformOrigin={{
                vertical: 'top',
                horizontal: 'right',
              }}
              open={open}
              onClose={handleClose}
            >
              <MenuItem onClick={handleClose}>Chess Club</MenuItem>
              <MenuItem onClick={handleClose}>NSFW Club</MenuItem>
            </Menu>
          </div>
        }
      </Toolbar>
    </AppBar>
  );
}

/**
 * @return {object} JSX
 */
function NestedList() {
  const classes = useStyles();
  const [openC, setOpenC] = React.useState(true);
  const [openM, setOpenM] = React.useState(true);

  const handleClickC = () => {
    setOpenC(!openC);
  };

  const handleClickM = () => {
    setOpenM(!openM);
  };

  return (
    <List
      position='fixed'
      className={classes.list}
      component="nav"
      aria-labelledby="nested-list-subheader">
      <ListItem button onClick={handleClickC}>
        {openC ? <ExpandLess /> : <ExpandMore />}
        <ListItemText primary="Channels" />
      </ListItem>
      <Collapse in={openC} timeout="auto" unmountOnExit>
        <List component="div" disablePadding>
          <ListItem button className={classes.nested}>
            <ListItemIcon>
              <BlurOnOutlinedIcon />
            </ListItemIcon>
            <ListItemText primary="Assignment 1" />
          </ListItem>
        </List>
      </Collapse>
      <ListItem button onClick={handleClickM}>
        {openM ? <ExpandLess /> : <ExpandMore />}
        <ListItemText primary="Direct Messages" />
      </ListItem>
      <Collapse in={openM} timeout="auto" unmountOnExit>
        <List component="div" disablePadding>
          <ListItem button className={classes.nested}>
            <ListItemIcon>
              <AccountCircleTwoToneIcon />
            </ListItemIcon>
            <ListItemText primary="David Henry" />
          </ListItem>
        </List>
      </Collapse>
    </List>
  );
}

/**
 * @return {object} JSX
 */
function LabelBottomNavigation() {
  const classes = useStyles();
  const [value, setValue] = React.useState('recents');

  const handleChange = (event, newValue) => {
    setValue(newValue);
  };

  return (
    <BottomNavigation
      value={value}
      onChange={handleChange}
      className={classes.bottom}
    >
      <BottomNavigationAction
        label="Home"
        value="home"
        icon={<HomeTwoToneIcon />}
      />
      <BottomNavigationAction
        label="Messages"
        value="messages"
        icon={<ForumTwoToneIcon />}
      />
      <BottomNavigationAction
        label="Mentions"
        value="mentions"
        icon={<AlternateEmailTwoToneIcon />}
      />
      <BottomNavigationAction
        label="Search"
        value="search"
        icon={<SearchTwoToneIcon />}
      />
      <BottomNavigationAction
        label="Profile"
        value="profile"
        icon={<PersonOutlineTwoToneIcon />}
      />
    </BottomNavigation>
  );
}

export default Homepage;
