import React from 'react';
import AppBar from '@material-ui/core/AppBar';
import {makeStyles} from '@material-ui/core/styles';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import AccountCircle from '@material-ui/icons/AccountCircle';
import MenuItem from '@material-ui/core/MenuItem';
import Menu from '@material-ui/core/Menu';
import ArrowDropDownCircleIcon from '@material-ui/icons/ArrowDropDownCircle';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';
import Collapse from '@material-ui/core/Collapse';
import ExpandLess from '@material-ui/icons/ExpandLess';
import ExpandMore from '@material-ui/icons/ExpandMore';
import BlurOnOutlinedIcon from '@material-ui/icons/BlurOnOutlined';
import BottomNavigation from '@material-ui/core/BottomNavigation';
import BottomNavigationAction from '@material-ui/core/BottomNavigationAction';
import HomeTwoToneIcon from '@material-ui/icons/HomeTwoTone';
import ForumTwoToneIcon from '@material-ui/icons/ForumTwoTone';
import AlternateEmailTwoToneIcon
  from '@material-ui/icons/AlternateEmailTwoTone';
import SearchTwoToneIcon from '@material-ui/icons/SearchTwoTone';
import PersonOutlineTwoToneIcon from '@material-ui/icons/PersonOutlineTwoTone';
import AccountCircleTwoToneIcon from '@material-ui/icons/AccountCircleTwoTone';
import { channel } from 'node:diagnostic_channel';
// import Badge from "@material-ui/core/Badge";
// import Avatar from "@material-ui/core/Avatar";

const useStyles = makeStyles((theme) => ({
  appBar: {
    flexGrow: 1,
    width: `100%`,
    margin: `0px`,
    background: `#4A154B`,
    height: `50px`,
  },
  toolbar: theme.mixins.toolbar,
  menuButton: {
    marginRight: theme.spacing(1),
    marginBottom: theme.spacing(1),
    // height: 40,
  },
  title: {
    flexGrow: 1,
    fontSize: `18px`,
    height: 40,
    fontFamily: 'Verdana',
    // fontWeight: 'bold',
  },
  list: {
    marginTop: 50,
    width: `100%`,
    backgroundColor: theme.palette.background.paper,
  },
  nested: {
    paddingLeft: theme.spacing(4),
  },
  bottom: {
    width: '95%',
    position: 'fixed',
    bottom: 0,
  },
}));

/**
 * @param {*} setWorkspace
 * @param {*} setError
 */
const fetchWorkspace = (setWorkspace) => { // setError
  const item = localStorage.getItem('user');
  if (!item) {
    return;
  }
  const user = JSON.parse(item);
  const bearerToken = user ? user.accessToken : '';
  fetch('/v0/workspace', {
    method: 'get',
    headers: new Headers({
      'Authorization': `Bearer ${bearerToken}`,
      'Content-Type': 'application/x-www-form-urlencoded',
    }),
  })
    .then((response) => {
      if (!response.ok) {
        throw response;
      }
      return response.json();
    })
    .then((arr) => {
      setWorkspace(arr);
      // setError('');
    })
    .catch((error) => {
      console.log(error);
      setWorkspace([]);
      // setError('There is an Error!');
    });
};

const fetchChannels = (showChannels) => {
  const item = localStorage.getItem('user');
  if (!item) {
    return;
  }
  const user = JSON.parse(item);
  const bearerToken = user ? user.accessToken : '';
  fetch('/v0/channel/:workspace', {
    method: 'get',
    headers: new Headers({
      'Authorization': `Bearer ${bearerToken}`,
      'Content-Type': 'application/x-www-form-urlencoded',
    }),
  })
    .then((response) => {
      if (!response.ok) {
        throw response;
      }
      return response.json();
    })
    .then((arr) => {
      showChannels(arr);
      setError('');
    })
    .catch((error) => {
      console.log(error);
      showChannels([]);
      setError('There is an Error!');
    });
};

/**
 * @return {object} JSX
 */
function Homepage() {
  return (
    <div>
      <MenuAppBar/>
      <NestedList/>
      <LabelBottomNavigation/>
    </div>
  );
}

/**
 * @return {*}
 */
function MenuAppBar() {
  const classes = useStyles();
  const [anchorEl, setAnchorEl] = React.useState(null);
  const open = Boolean(anchorEl);

  // const user = JSON.parse(localStorage.getItem('user'));
  const [workspace, setWorkspace] = React.useState([]);
  // const [name, setName] = React.useState(user ? user.name : '');
  // const [error, setError] = React.useState('');

  // const logout = () => {
  //   localStorage.removeItem('user');
  //   setWorkspace([]);
  //   setName('');
  //   setError('Logged Out');
  // };

  React.useEffect(() => {
    fetchWorkspace(setWorkspace);
  }, []);

  const [currWS, setCurrWS] = React.useState(workspace[0]);

  const handleMenu = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleClose = (wsName) => {
    setAnchorEl(null);
    setCurrWS(wsName);
  };

  return (
    <AppBar
      position="fixed"
      className={classes.appBar}
      elevation={3}>
      <Toolbar className={classes.toolbar}>
        <Typography variant="h6" className={classes.title}>
          {currWS}
        </Typography>
        {
          <div>
            <ArrowDropDownCircleIcon
              className={classes.menuButton}
              aria-label="account of current user"
              aria-controls="menu-appbar"
              aria-haspopup="true"
              onClick={handleMenu}
              color="inherit"
            >
              <AccountCircle />
            </ArrowDropDownCircleIcon>
            <Menu
              id="menu-appbar"
              anchorEl={anchorEl}
              anchorOrigin={{
                vertical: 'top',
                horizontal: 'right',
              }}
              keepMounted
              transformOrigin={{
                vertical: 'top',
                horizontal: 'right',
              }}
              open={open}
              onClose={handleClose}
            >
              {workspace.map( (ws) => (
                <MenuItem
                  key={ws.name}
                  onClick={() => {
                    handleClose(ws);
                  }}>
                  {ws}
                </MenuItem>
              ))}
            </Menu>
          </div>
        }
      </Toolbar>
    </AppBar>
  );
}

/**
 * @return {object} JSX
 */
function NestedList() {
  const classes = useStyles();
  const [channelOpen, setChannelOpen] = React.useState(true);
  const [msgOpen, setMsgOpen] = React.useState(true);

  const toggleOpenChannels = () => {
    setChannelOpen(!channelOpen);
  };

  const toggleOpenMsg = () => {
    setMsgOpen(!msgOpen);
  };

  React.useEffect(() => {
    fetchChannels(setChannels);
  }, []);

  const allChannels = () => {
    const channelsInWorkspace = [];
    for (let i = 0; i < 1; i++) {
      const jsx = 
      <List component="div" disablePadding>
        <ListItem
          key={'assignment1'}
          button
          className={classes.nested}>
          <ListItemIcon>
            <BlurOnOutlinedIcon />
          </ListItemIcon>
          <ListItemText primary='Assignment 1' />
        </ListItem>
      </List>;
      list.push(jsx);
    }
    return channelsInWorkspace;
  };

  return (
    <List
      position='fixed'
      className={classes.list}
      component="nav"
      aria-labelledby="nested-list-subheader">
      <ListItem
        key={'channels'}
        button
        onClick={toggleOpenChannels}>
        {channelOpen ? <ExpandLess /> : <ExpandMore />}
        <ListItemText primary="Channels" />
      </ListItem>
      <Collapse in={channelOpen} timeout="auto" unmountOnExit>
        {allChannels}
      </Collapse>
      <ListItem
        key={'directMessages'}
        button
        onClick={toggleOpenMsg}>
        {msgOpen ? <ExpandLess /> : <ExpandMore />}
        <ListItemText primary="Direct Messages" />
      </ListItem>
      <Collapse in={msgOpen} timeout="auto" unmountOnExit>
        <List component="div" disablePadding>
          <ListItem
            key={'davidHenry'}
            button
            className={classes.nested}>
            <ListItemIcon>
              <AccountCircleTwoToneIcon />
            </ListItemIcon>
            <ListItemText primary="David Henry" />
          </ListItem>
        </List>
      </Collapse>
    </List>
  );
}

