//   if (!item) {
//     return;
//   }
//   const user = JSON.parse(item);
//   const bearerToken = user ? user.accessToken : '';
//   fetch('/v0/channel/:workspace', {
//     method: 'get',
//     headers: new Headers({
//       'Authorization': `Bearer ${bearerToken}`,
//       'Content-Type': 'application/x-www-form-urlencoded',
//     }),
//   })
//     .then((response) => {
//       if (!response.ok) {
//         throw response;
//       }
//       return response.json();
//     })
//     .then((arr) => {
//       showChannels(arr);
//       setError('');
//     })
//     .catch((error) => {
//       console.log(error);
//       showChannels([]);
//       setError('There is an Error!');
//     });
// };

/**
 * @return {object} JSX
 */
function Homepage() {
  return (
    <div>
      <MenuAppBar/>
      <NestedList/>
      <LabelBottomNavigation/>
    </div>
  );
}

/**
 * @return {*}
 */
function MenuAppBar() {
  const classes = useStyles();
  const [anchorEl, setAnchorEl] = React.useState(null);
  const open = Boolean(anchorEl);

  const [workspace, setWorkspace] = React.useState([]);

  React.useEffect(() => {
    fetchWorkspace(setWorkspace);
  }, []);

  console.log(workspace);

  const [currWS, setCurrWS] = React.useState(workspace[0]);

  const handleMenu = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleClose = (wsName) => {
    setAnchorEl(null);
    setCurrWS(wsName);
  };

  return (
    <AppBar
      position="fixed"
      className={classes.appBar}
      elevation={3}>
      <Toolbar className={classes.toolbar}>
        <Typography variant="h6" className={classes.title}>
          {currWS}
        </Typography>
        {
          <div>
            <ArrowDropDownCircleIcon
              className={classes.menuButton}
              aria-label="account of current user"
              aria-controls="menu-appbar"
              aria-haspopup="true"
              onClick={handleMenu}
              color="inherit"
            >
              <AccountCircle />
            </ArrowDropDownCircleIcon>
            <Menu
              id="menu-appbar"
              anchorEl={anchorEl}
              anchorOrigin={{
                vertical: 'top',
                horizontal: 'right',
              }}
              keepMounted
              transformOrigin={{
                vertical: 'top',
                horizontal: 'right',
              }}
              open={open}
              onClose={handleClose}
            >
              {workspace.map( (ws) => (
                <MenuItem
                  key={ws.name}
                  onClick={() => {
                    handleClose(ws);
                  }}>
                  {ws}
                </MenuItem>
              ))}
            </Menu>
          </div>
        }
      </Toolbar>
    </AppBar>
  );
}

/**
 * @return {object} JSX
 */
function NestedList() {
  const classes = useStyles();
  const [channelOpen, setChannelOpen] = React.useState(true);
  const [msgOpen, setMsgOpen] = React.useState(true);

  const toggleOpenChannels = () => {
    setChannelOpen(!channelOpen);
  };

  const toggleOpenMsg = () => {
    setMsgOpen(!msgOpen);
  };

  // React.useEffect(() => {
  //   fetchChannels(setChannels);
  // }, []);

  return (
    <List
      position='fixed'
      className={classes.list}
      component="nav"
      aria-labelledby="nested-list-subheader">
      <ListItem
        key={'channels'}
        button
        onClick={toggleOpenChannels}>
        {channelOpen ? <ExpandLess /> : <ExpandMore />}
        <ListItemText primary="Channels" />
      </ListItem>
      <Collapse in={channelOpen} timeout="auto" unmountOnExit>
        <List component="div" disablePadding>
          <ListItem
            key={'asg1'}
            button
            className={classes.nested}>
            <ListItemIcon>
              <BlurOnOutlinedIcon />
            </ListItemIcon>
            <ListItemText primary='Assignment 1' />
          </ListItem>
        </List>
      </Collapse>
      <ListItem
        key={'directMessages'}
        button
        onClick={toggleOpenMsg}>
        {msgOpen ? <ExpandLess /> : <ExpandMore />}
        <ListItemText primary="Direct Messages" />
      </ListItem>
      <Collapse in={msgOpen} timeout="auto" unmountOnExit>
        <List component="div" disablePadding>
          <ListItem
            key={'davidHenry'}
            button
            className={classes.nested}>
            <ListItemIcon>
              <AccountCircleTwoToneIcon />
            </ListItemIcon>
            <ListItemText primary="David Henry" />
          </ListItem>
        </List>
      </Collapse>
    </List>
  );
}

/**
 * @return {object} JSX
 */
function LabelBottomNavigation() {
  const classes = useStyles();
  const [value, setValue] = React.useState('recents');

  const handleChange = (event, newValue) => {
    setValue(newValue);
  };

  return (
    <BottomNavigation
      value={value}
      onChange={handleChange}
      className={classes.bottom}
    >
      <BottomNavigationAction
        label="Home"
