import React from 'react';
import {makeStyles} from '@material-ui/core/styles';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';
import Collapse from '@material-ui/core/Collapse';
import ExpandLess from '@material-ui/icons/ExpandLess';
import ExpandMore from '@material-ui/icons/ExpandMore';
import BlurOnOutlinedIcon from '@material-ui/icons/BlurOnOutlined';
import AccountCircleTwoToneIcon from '@material-ui/icons/AccountCircleTwoTone';

import {WorkspaceContext} from '../Homepage';

const useStyles = makeStyles((theme) => ({
  list: {
    position: 'fixed',
    marginTop: 50,
    width: `100%`,
    backgroundColor: theme.palette.background.paper,
  },
  nested: {
    paddingLeft: theme.spacing(4),
  },
}));

const fetchChannels = (setChannelList, workspaceID) => {
  const item = localStorage.getItem('user');
  if (!item) {
    return;
  }
  const user = JSON.parse(item);
  const bearerToken = user ? user.accessToken : '';
  if (workspaceID === undefined) {
    return;
  }
  fetch(`/v0/channel/${workspaceID}`, {
    method: 'get',
    headers: new Headers({
      'Authorization': `Bearer ${bearerToken}`,
      'Content-Type': 'application/x-www-form-urlencoded',
    }),
  })
    .then((response) => {
      if (!response.ok) {
        throw response;
      }
      return response.json();
    })
    .then((arr) => {
      setChannelList(arr);
    })
    .catch((error) => {
      console.log(error);
      setChannelList([]);
    });
};

const fetchUsers = (setUserList, workspaceID) => {
  const item = localStorage.getItem('user');
  if (!item) {
    return;
  }
  const user = JSON.parse(item);
  const bearerToken = user ? user.accessToken : '';
  if (workspaceID === undefined) {
    return;
  }
  fetch(`/v0/user/${workspaceID}`, {
    method: 'get',
    headers: new Headers({
      'Authorization': `Bearer ${bearerToken}`,
      'Content-Type': 'application/x-www-form-urlencoded',
    }),
  })
    .then((response) => {
      if (!response.ok) {
        throw response;
      }
      return response.json();
    })
    .then((arr) => {
      setUserList(arr);
    })
    .catch((error) => {
      console.log(error);
      setUserList([]);
    });
};

/**
 * @return {object} JSX
 */
function NestedList() {
  const classes = useStyles();

  const {value, value2} = React.useContext(WorkspaceContext);
  const [workspaceList] = value;
  const [workspace] = value2;

  const [channelList, setChannelList] = React.useState([]);
  const [channelOpen, setChannelOpen] = React.useState(true);
  const [msgOpen, setMsgOpen] = React.useState(true);

  const [userList, setUserList] = React.useState([]);

  const toggleOpenChannels = () => {
    setChannelOpen(!channelOpen);
  };

  const toggleOpenMsg = () => {
    setMsgOpen(!msgOpen);
  };

  const workspaceID = () => {
    for (let i = 0; i < workspaceList.length; i++) {
      if (workspace === workspaceList[i].name) {
        return workspaceList[i].id;
      }
    }
  };

  const wsID = workspaceID();

  React.useEffect(() => {
    fetchChannels(setChannelList, wsID);
  }, [wsID]);

  React.useEffect(() => {
    fetchUsers(setUserList, wsID);
  }, [wsID]);

  const displayChannels = () => {
    const arr = [];
    for (let i = 0; i < channelList.length; i++) {
      const name = channelList[i]['name'];
      const jsx =
        <ListItem
          key={name}
          button
          className={classes.nested}>
          <ListItemIcon>
            <BlurOnOutlinedIcon />
          </ListItemIcon>
          <ListItemText primary={name} />
        </ListItem>;
      arr.push(jsx);
    }
    return arr;
  };

  const displayUsers = () => {
    const arr = [];
    for (let i = 0; i < userList.length; i++) {
      const name = userList[i]['name'];
      const jsx =
        <ListItem
          key={name}
          button
          className={classes.nested}>
          <ListItemIcon>
            <AccountCircleTwoToneIcon />
          </ListItemIcon>
          <ListItemText primary={name} />
        </ListItem>
      arr.push(jsx);
    }
    return arr;
  };

  return (
    <List
      className={classes.list}
      component="nav"
      aria-labelledby="nested-list-subheader">
      <ListItem
        key={'channels'}
        button
        onClick={toggleOpenChannels}>
        {channelOpen ? <ExpandLess /> : <ExpandMore />}
        <ListItemText primary="Channels" />
      </ListItem>
      <Collapse in={channelOpen} timeout="auto" unmountOnExit>
        <List component="div" disablePadding>
          {displayChannels()}
        </List>
      </Collapse>
      <ListItem
        key={'directMessages'}
        button
        onClick={toggleOpenMsg}>
        {msgOpen ? <ExpandLess /> : <ExpandMore />}
        <ListItemText primary="Direct Messages" />
      </ListItem>
      <Collapse in={msgOpen} timeout="auto" unmountOnExit>
        <List component="div" disablePadding>
          {displayUsers()}
        </List>
      </Collapse>
    </List>
  );
}

export default NestedList;
