    margin: `0px`,
    background: `#4A154B`,
    height: `50px`,
  },
  toolbar: theme.mixins.toolbar,
  menuButton: {
    marginRight: theme.spacing(1),
    marginBottom: theme.spacing(1),
  },
  title: {
    flexGrow: 1,
    fontSize: `18px`,
    height: 40,
    fontFamily: 'Verdana',
  },
  list: {
    marginTop: 50,
    width: `100%`,
    backgroundColor: theme.palette.background.paper,
  },
  nested: {
    paddingLeft: theme.spacing(4),
  },
  bottom: {
    width: '95%',
    position: 'fixed',
    bottom: 0,
  },
}));

/**
 * @param {*} setWorkspace
 */
const fetchWorkspace = (setWorkspace) => {
  const item = localStorage.getItem('user');
  if (!item) {
    return;
  }
  const user = JSON.parse(item);
  const bearerToken = user ? user.accessToken : '';
  fetch('/v0/workspace', {
    method: 'get',
    headers: new Headers({
      'Authorization': `Bearer ${bearerToken}`,
      'Content-Type': 'application/x-www-form-urlencoded',
    }),
  })
    .then((response) => {
      if (!response.ok) {
        throw response;
      }
      return response.json();
    })
    .then((arr) => {
      setWorkspace(arr);
    })
    .catch((error) => {
      console.log(error);
      setWorkspace([]);
    });
};

