import React from 'react';
import {makeStyles} from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import SendIcon from '@material-ui/icons/Send';
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';
import ListItemAvatar from '@material-ui/core/ListItemAvatar';
import Avatar from '@material-ui/core/Avatar';

import {WorkspaceContext} from '../Homepage';

const useStyles = makeStyles((theme) => ({
  text: {
    '& > *': {
      margin: theme.spacing(1),
    },
    'position': 'fixed',
    'bottom': 60,
    'width': '100%',
  },
  send: {
    'position': 'fixed',
    'bottom': 84,
    'right': 15,
    'zIndex': 1300,
  },
  inline: {
    display: 'block',
    fontWeight: 'bold',
  },
}));

/**
 * @return {any} jsx
 */
function SendText() {
  const classes = useStyles();

  const {value5} = React.useContext(WorkspaceContext);
  const [channelFeed, setChannelFeed] = value5;

  const item = localStorage.getItem('user');
  const user = JSON.parse(item);

  const createUUID = () => { // https://www.tutorialspoint.com/how-to-create-guid-uuid-in-javascript
    return 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, function(c) {
      const r = Math.random() * 16 | 0;
      const v = (c === 'x') ? r : (r & 0x3 | 0x8);
      return v.toString(16);
    });
  };

  const sendMsg = (content) => {
    const msgList = channelFeed;
    const name = user.name;
    const d = new Date();
    const date = `${d.getHours()}:${d.getMinutes()}`;
    const id = createUUID();
    console.log(id);
    const newMsg =
      <ListItem
        button
        key={id}
        alignItems="flex-start">
        <ListItemAvatar>
          <Avatar alt="Remy Sharp"/>
        </ListItemAvatar>
        <ListItemText
          primary={
            <React.Fragment>
              <Grid container justifyContent="space-between">
                <Typography
                  component="span"
                  variant="body1"
                  className={classes.inline}
                  color="textPrimary"
                >{name}</Typography>
                <Typography
                  className={classes.inline}
                  color="textSecondary"
                  align='right'>{date}
                </Typography>
              </Grid>
            </React.Fragment>
          }
          secondary={
            <React.Fragment>
              <Typography
                component="span"
                variant="body1"
                color="textPrimary"
              >{content}</Typography>
            </React.Fragment>
          }
        />
      </ListItem>;
    msgList.push(newMsg);
    setChannelFeed(msgList);
  };

  return (
    <form>
      {/* https://material-ui.com/components/text-fields/ */}
      <TextField
        className={classes.text}
        id="outlined-basic"
        variant="outlined"
        placeholder="Send Message..."
      />
      {/* https://material-ui.com/components/material-icons/ */}
      <SendIcon
        className={classes.send}
        onClick={sendMsg('Hello!')}
      />
    </form>
  );
}

export default SendText;
