import React from 'react';
import {makeStyles} from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import SendIcon from '@material-ui/icons/Send';
import {WorkspaceContext} from '../Homepage';

const useStyles = makeStyles((theme) => ({
  text: {
    '& > *': {
      margin: theme.spacing(1),
    },
    'position': 'fixed',
    'bottom': 60,
    'width': '100%',
  },
  send: {
    'position': 'fixed',
    'bottom': 84,
    'right': 15,
    'zIndex': 1300,
  },
  inline: {
    display: 'block',
    fontWeight: 'bold',
  },
}));

/**
 * @return {any} jsx
 */
function SendText() {
  const classes = useStyles();

  const {value5} = React.useContext(WorkspaceContext);
  const [channelFeed, setChannelFeed] = value5;

  const item = localStorage.getItem('user');
  const user = JSON.parse(item);

  // https://www.tutorialspoint.com/how-to-create-guid-uuid-in-javascript
  const createUUID = () => {
    return 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, function(c) {
      const r = Math.random() * 16 | 0;
      const v = (c === 'x') ? r : ((r & 0x3) | 0x8);
      return v.toString(16);
    });
  };

  const sendMsg = (content) => {
    const msgList = channelFeed;
    const d = new Date();
    const id = createUUID();
    const dict = {'name': user.name, 'date': d};
    dict['content'] = content;
    dict['id'] = id;
    console.log(dict);
    msgList.push(dict);
    setChannelFeed(msgList);
  };

  return (
    <form>
      {/* https://material-ui.com/components/text-fields/ */}
      <TextField
        className={classes.text}
        id="outlined-basic"
        variant="outlined"
        placeholder="Send Message..."
      />
      {/* https://material-ui.com/components/material-icons/ */}
      <SendIcon
        className={classes.send}
        onClick={() => sendMsg('Hello!')}
      />
    </form>
  );
}

export default SendText;
