import React from 'react';
import AppBar from '@material-ui/core/AppBar';
import {makeStyles} from '@material-ui/core/styles';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import AccountCircle from '@material-ui/icons/AccountCircle';
import MenuItem from '@material-ui/core/MenuItem';
import Menu from '@material-ui/core/Menu';
import ArrowDropDownCircleIcon from '@material-ui/icons/ArrowDropDownCircle';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';
import Collapse from '@material-ui/core/Collapse';
import ExpandLess from '@material-ui/icons/ExpandLess';
import ExpandMore from '@material-ui/icons/ExpandMore';
import BlurOnOutlinedIcon from '@material-ui/icons/BlurOnOutlined';
import BottomNavigation from '@material-ui/core/BottomNavigation';
import BottomNavigationAction from '@material-ui/core/BottomNavigationAction';
import HomeTwoToneIcon from '@material-ui/icons/HomeTwoTone';
import ForumTwoToneIcon from '@material-ui/icons/ForumTwoTone';
import AlternateEmailTwoToneIcon
  from '@material-ui/icons/AlternateEmailTwoTone';
import SearchTwoToneIcon from '@material-ui/icons/SearchTwoTone';
import PersonOutlineTwoToneIcon from '@material-ui/icons/PersonOutlineTwoTone';
import AccountCircleTwoToneIcon from '@material-ui/icons/AccountCircleTwoTone';

const useStyles = makeStyles((theme) => ({
  appBar: {
    flexGrow: 1,
    width: `100%`,
    margin: `0`,
    background: `#4A154B`,
    height: `50px`,
  },
  toolbar: theme.mixins.toolbar,
  menuButton: {
    marginRight: theme.spacing(1),
    marginBottom: theme.spacing(1),
  },
  title: {
    flexGrow: 1,
    fontSize: `20px`,
    height: 40,
    fontFamily: 'Verdana',
  },
  list: {
    marginTop: 50,
    width: `100%`,
    backgroundColor: theme.palette.background.paper,
  },
  nested: {
    paddingLeft: theme.spacing(4),
  },
  bottom: {
    width: '95%',
    position: 'fixed',
    bottom: 0,
  },
}));

/**
 * @param {*} setWorkspace
 */
const fetchWorkspace = (setWorkspace) => {
  const item = localStorage.getItem('user');
  if (!item) {
    return;
  }
  const user = JSON.parse(item);
  const bearerToken = user ? user.accessToken : '';
  fetch('/v0/workspace', {
    method: 'get',
    headers: new Headers({
      'Authorization': `Bearer ${bearerToken}`,
      'Content-Type': 'application/x-www-form-urlencoded',
    }),
  })
    .then((response) => {
      if (!response.ok) {
        throw response;
      }
      return response.json();
    })
    .then((arr) => {
      setWorkspace(arr);
    })
    .catch((error) => {
      console.log(error);
      setWorkspace([]);
    });
};

// const fetchChannels = (showChannels) => {
//   const item = localStorage.getItem('user');
//   if (!item) {
//     return;
//   }
//   const user = JSON.parse(item);
//   const bearerToken = user ? user.accessToken : '';
//   fetch('/v0/channel/:workspace', {
//     method: 'get',
//     headers: new Headers({
//       'Authorization': `Bearer ${bearerToken}`,
//       'Content-Type': 'application/x-www-form-urlencoded',
//     }),
//   })
//     .then((response) => {
//       if (!response.ok) {
//         throw response;
//       }
//       return response.json();
//     })
//     .then((arr) => {
//       showChannels(arr);
//     })
//     .catch((error) => {
//       console.log(error);
//       showChannels([]);
//     });
// };

/**
 * @return {object} JSX
 */
function Homepage() {
  return (
    <div>
      <MenuAppBar/>
      <NestedList/>
      <LabelBottomNavigation/>
    </div>
  );
}

/**
 * @return {*}
 */
function MenuAppBar() {
  const classes = useStyles();
  const [anchorEl, setAnchorEl] = React.useState(null);

  const [workspaceList, setWorkspaceList] = React.useState([]);
  const [workspace, setWorkspace] = React.useState('');

  React.useEffect(() => {
    fetchWorkspace(setWorkspaceList);
  }, []);

  React.useEffect((workspace) => {
    setWorkspace(workspace);
  }, workspace);

  console.log(workspaceList);

  const handleMenu = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleClose = () => {
    setAnchorEl(null);
  };

  const setNewWorkspace = (name) => {
    setWorkspace(name);
    setAnchorEl(null);
  };

  return (
    <div>
      <AppBar
        position="fixed"
        className={classes.appBar}
      >
        <Toolbar className={classes.toolbar}>
          <Typography variant="h6" className={classes.title} noWrap>
            {workspace}
          </Typography>
          <ArrowDropDownCircleIcon
            className={classes.menuButton}
            onClick={handleMenu}
          >
            <AccountCircle/>
          </ArrowDropDownCircleIcon>
          <Menu
            anchorEl={anchorEl}
            keepMounted
            open={Boolean(anchorEl)}
            onClose={handleClose}
          >
            {workspaceList.map((name) => (
              <MenuItem
                key={name}
                onClick={() => setNewWorkspace(name)}
              >
                {name}
              </MenuItem>
            ))}
          </Menu>
        </Toolbar>
      </AppBar>
    </div>
  );
}

/**
 * @return {object} JSX
 */
function NestedList() {
  const classes = useStyles();
  const [channelOpen, setChannelOpen] = React.useState(true);
  const [msgOpen, setMsgOpen] = React.useState(true);

  const toggleOpenChannels = () => {
    setChannelOpen(!channelOpen);
  };

  const toggleOpenMsg = () => {
    setMsgOpen(!msgOpen);
  };

  // React.useEffect(() => {
  //  fetchChannels(setChannelOpen);
  // }, []);

  return (
    <List
      position='fixed'
      className={classes.list}
      component="nav"
      aria-labelledby="nested-list-subheader">
      <ListItem
        key={'channels'}
        button
        onClick={toggleOpenChannels}>
        {channelOpen ? <ExpandLess /> : <ExpandMore />}
        <ListItemText primary="Channels" />
      </ListItem>
      <Collapse in={channelOpen} timeout="auto" unmountOnExit>
        <List component="div" disablePadding>
          <ListItem
            key={'asg1'}
            button
            className={classes.nested}>
            <ListItemIcon>
              <BlurOnOutlinedIcon />
            </ListItemIcon>
            <ListItemText primary='Assignment 1' />
          </ListItem>
        </List>
      </Collapse>
      <ListItem
        key={'directMessages'}
        button
        onClick={toggleOpenMsg}>
        {msgOpen ? <ExpandLess /> : <ExpandMore />}
        <ListItemText primary="Direct Messages" />
      </ListItem>
      <Collapse in={msgOpen} timeout="auto" unmountOnExit>
        <List component="div" disablePadding>
          <ListItem
            key={'davidHenry'}
            button
            className={classes.nested}>
            <ListItemIcon>
              <AccountCircleTwoToneIcon />
            </ListItemIcon>
            <ListItemText primary="David Henry" />
          </ListItem>
        </List>
      </Collapse>
    </List>
  );
}

/**
 * @return {object} JSX
 */
function LabelBottomNavigation() {
  const classes = useStyles();
  const [value, setValue] = React.useState('recents');

  const handleChange = (event, newValue) => {
    setValue(newValue);
  };

  return (
    <BottomNavigation
      value={value}
      onChange={handleChange}
      className={classes.bottom}
    >
      <BottomNavigationAction
        label="Home"
        value="home"
        icon={<HomeTwoToneIcon />}
      />
      <BottomNavigationAction
        label="Messages"
        value="messages"
        icon={<ForumTwoToneIcon />}
      />
      <BottomNavigationAction
        label="Mentions"
        value="mentions"
        icon={<AlternateEmailTwoToneIcon />}
      />
      <BottomNavigationAction
        label="Search"
        value="search"
        icon={<SearchTwoToneIcon />}
      />
      <BottomNavigationAction
        label="Profile"
        value="profile"
        icon={<PersonOutlineTwoToneIcon />}
      />
    </BottomNavigation>
  );
}

export default Homepage;
